import React from 'react';

import './App.css';
import './styles/theme.css';
import Header from './components/Header';


function App() {
  return (
    <div className="eoex-app">
      <Header />
      {/* Main content will go here */}
      <main style={{ padding: 'var(--section-spacing) 0', maxWidth: 'var(--max-content-width)', margin: '0 auto' }}>
        <h1 style={{ fontFamily: 'Google Sans, Arial, sans-serif', fontWeight: 600, fontSize: '2.5rem', marginBottom: '1rem' }}>Play more. Discover more.</h1>
        <p style={{ fontSize: '1.25rem', color: 'var(--text-secondary)' }}>Welcome to EOEX Store — your destination for games and apps, inspired by Google Play’s best design patterns.</p>
      </main>
    </div>
  );
}

export default App;
